import {Component, OnDestroy, OnInit} from '@angular/core';
import {HardwareAddEditService} from "../../../services/hardware/hardware-add-edit.service";
import {Subscription} from "rxjs";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {HardwareDTO} from "../dto/hardware";
import * as _ from 'underscore';
import {Location} from "@angular/common";

@Component({
  selector: 'app-hardware-add-edit',
  templateUrl: './hardware-add-edit.component.html',
  styleUrls: ['./hardware-add-edit.component.css']
})
export class HardwareAddEditComponent implements OnInit, OnDestroy{

  hardwareForm : FormGroup;
  hardware = new HardwareDTO();
  pageType: string;

  onHardwareChangeSub = new Subscription();

  constructor(
    private hardwareAddEditService : HardwareAddEditService,
    private formBuilder : FormBuilder,
    private location : Location

  ){}

  ngOnInit(): void {
    this.onHardwareChangeSub = this.hardwareAddEditService.hardware
      .subscribe((hardware) => {
        if (!_.isEmpty(hardware)){
          console.log(hardware);
          this.hardware = new HardwareDTO(hardware);
          this.pageType ='edit';
        }else {
          this.hardware = new HardwareDTO();
          this.pageType = 'new';
        }
      // console.log("hardware" , this.hardware)
        this.hardwareForm = this.createForm();
    })
  }

  ngOnDestroy(): void {
    this.onHardwareChangeSub.unsubscribe();
  }

  createForm(){
    return this.formBuilder.group({
      hardwareID: [this.hardware.hardwareID],
      hardwareName: [this.hardware.hardwareName,[Validators.required]],
      hardwareAddress : [this.hardware.hardwareAddress,[Validators.required]],
      hardwareTel : [this.hardware.hardwareTel,[Validators.required]],
      hardwareEmail : [this.hardware.hardwareEmail,[Validators.required]],
      hardwareStatus : [this.hardware.hardwareStatus,[Validators.required]]
    })
  }

  isValid(){
    return this.hardwareForm.valid;
  }

  isDirty(){
    return this.hardwareForm.dirty;
  }

  goBack(){
    // console.log("click")
    // this.router.navigate(['admin/orders']) //using router link
    this.location.back();
  }

  saveHardware(){
    let hardware = this.hardwareForm.getRawValue();
    console.log("get data", hardware);
    this.hardwareAddEditService.saveHardware(hardware);
    if (hardware){
      alert("Data Saved");
      this.location.back()
    }

  }

}



  <mat-card-header>
    <mat-card-title>

      <div style="display: flex">

      <svg xmlns="http://www.w3.org/2000/svg" class="back-btn" (click)="goBack()" viewBox="0 0 512 512"><!--! Font Awesome Pro 6.3.0 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2023 Fonticons, Inc. --><path d="M512 256A256 256 0 1 0 0 256a256 256 0 1 0 512 0zM231 127c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9l-71 71L376 232c13.3 0 24 10.7 24 24s-10.7 24-24 24l-182.1 0 71 71c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0L119 273c-9.4-9.4-9.4-24.6 0-33.9L231 127z"/></svg>


      <span *ngIf="pageType == 'edit'">
        <h1>Edit Hardware</h1>
      </span>
      <span *ngIf="pageType == 'new'">
        <h1>New Hardware</h1>
      </span>

      </div>
    </mat-card-title>
  </mat-card-header>

  <br>

  <mat-card-content>

    <form [formGroup]="hardwareForm">

      <div class="mb-3" style="width: 98%;margin-left: 1%">
        <label  class="form-label">Hardware Name</label>
        <input class="form-control"  placeholder="Ex: XXXXX Hardware" formControlName="hardwareName">
      </div>

      <div class="d-flex justify-content-around">

        <div class="mb-3" style="width: 48%">
          <label  class="form-label">Hardware Address</label>
          <textarea class="form-control"  rows="5" placeholder="Ex: Kurunegala" formControlName="hardwareAddress"></textarea>
        </div>

        <div style="width: 48%">
          <div class="mb-3" >
            <label  class="form-label">Hardware Tel</label>
            <input type="text" class="form-control"  placeholder="Ex: 077XXXXXXX" formControlName="hardwareTel">
          </div>
          <div class="mb-3" >
            <label  class="form-label">Hardware Email</label>
            <input type="email" class="form-control"  placeholder="Ex: name@example.com" formControlName="hardwareEmail">
          </div>
        </div>
      </div>

      <div style="margin-bottom: 2%;width: 99%" class="d-flex justify-content-between">
      <div style="width: 48%; margin-left: 1%">
        <select class="form-select" aria-label="Default select example" formControlName="hardwareStatus">
          <option value="ACTIVE">ACTIVE</option>
          <option value="DEACTIVE">DEACTIVE</option>
        </select>
      </div>
        <div>

        </div>
      </div>

      <div style="margin-bottom: 2%; float: right">
        <button type="button" class="btn btn-primary save-btn" *ngIf="pageType == 'edit'" (click)="saveHardware()" [disabled]="!isDirty()">
          Update
        </button>
        <button type="button" class="btn btn-primary save-btn" *ngIf="pageType == 'new'" (click)="saveHardware()" [disabled]="!isValid()">
          Save
        </button>
      </div>

    </form>

  </mat-card-content>

