import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {DashboardService} from "../../services/dashboard/dashboard.service";

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{

  dashboardValues : any;


  constructor(private dashboardService : DashboardService,
              private router :Router,
  ){}

  ngOnInit(): void {
    this.dashboardService.getValues();
    this.dashboardService.dashboardValues
      .subscribe((values) =>{
        // console.log("values in dashboard",values);
        if (values){
          this.dashboardValues = values;
        }
      })

  }

  getValues(){
    this.dashboardService.getValues();
  }


}
<p>Counts</p>

<div style="display: flex; margin-bottom: 2%" >

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded order-count" style="background-color: cadetblue">
    <div class="card-body">
      <div>
      <h1>Orders</h1>
      <p>{{dashboardValues?.orderCount}}</p>
      </div>
      <div style="width: 50%">
        <svg xmlns="http://www.w3.org/2000/svg" style="color: white" viewBox="0 0 576 512"><!--! Font Awesome Pro 6.4.0 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2023 Fonticons, Inc. --><path d="M0 96C0 60.7 28.7 32 64 32H512c35.3 0 64 28.7 64 64V416c0 35.3-28.7 64-64 64H64c-35.3 0-64-28.7-64-64V96zM128 288a32 32 0 1 0 0-64 32 32 0 1 0 0 64zm32-128a32 32 0 1 0 -64 0 32 32 0 1 0 64 0zM128 384a32 32 0 1 0 0-64 32 32 0 1 0 0 64zm96-248c-13.3 0-24 10.7-24 24s10.7 24 24 24H448c13.3 0 24-10.7 24-24s-10.7-24-24-24H224zm0 96c-13.3 0-24 10.7-24 24s10.7 24 24 24H448c13.3 0 24-10.7 24-24s-10.7-24-24-24H224zm0 96c-13.3 0-24 10.7-24 24s10.7 24 24 24H448c13.3 0 24-10.7 24-24s-10.7-24-24-24H224z" fill="white" fill-opacity="75%"/></svg>
      </div>

    </div>
  </div>

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded hardware-count" style="background-color: aquamarine">
    <div class="card-body">
      <h1>Hardware Stores</h1>
      <p>{{dashboardValues?.hardwareCount}}</p>
    </div>
  </div>

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded product-count" style="background-color: aqua">
    <div class="card-body">
      <h1>Products</h1>
      <p>{{dashboardValues?.productCount}}</p>
    </div>
  </div>

</div>

<p>Counts</p>

<div style="display: flex; margin-bottom: 2%">

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded total-revenue" style="background-color: cadetblue">
    <div class="card-body">
      <h1>Total Revenue</h1>
      <p> LKR {{dashboardValues?.totalRevenue}}</p>
    </div>
  </div>

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded cash-revenue" style="background-color: cadetblue">
    <div class="card-body">
      <h1>Cash Revenue</h1>
      <p> LKR {{dashboardValues?.cashRevenue}}</p>
    </div>
  </div>

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded cheque-revenue" style="background-color: cadetblue">
    <div class="card-body">
      <h1>Cheque Revenue</h1>
      <p> LKR {{dashboardValues?.chequeRevenue}}</p>
    </div>
  </div>

  <div class="card mb-4 border-0 shadow p-3 mb-5 bg-body rounded credit-revenue" style="background-color: cadetblue">
    <div class="card-body">
      <h1>Credit Revenue</h1>
      <p> LKR {{dashboardValues?.creditRevenue}}</p>
    </div>
  </div>

</div>
