import {Component, OnDestroy, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {OrderDTO} from "../dto/OrderDTO";
import {Subscription} from "rxjs";
import {OrderAddEditService} from "../../../services/order/order-add-edit.service";
import * as _ from 'underscore';

@Component({
  selector: 'app-order-add-edit',
  templateUrl: './order-add-edit.component.html',
  styleUrls: ['./order-add-edit.component.css']
})
export class OrderAddEditComponent implements OnInit,OnDestroy{


  orderForm : FormGroup;
  order = new OrderDTO();
  pageType: string;

  onOrderChangeSub = new Subscription();

  constructor(
    private orderAddEditService : OrderAddEditService,
    private formBuilder : FormBuilder

  ){}

  ngOnInit(): void {

    this.orderAddEditService.onProductsChanged
      .subscribe((products)=>{
        console.log(products)
      });

    this.orderAddEditService.onHardwaresChanged
      .subscribe((hardwares)=>{
        console.log(hardwares)
      });

    this.onOrderChangeSub = this.orderAddEditService.order
      .subscribe((order) => {
        if (!_.isEmpty(order)){
          console.log(order);
          this.order = new OrderDTO(order);
          this.pageType ='edit';
        }else {
          this.order = new OrderDTO(order);
          this.pageType = 'new';
        }
        // console.log("hardware" , this.hardware)
        this.orderForm = this.createForm();
      })
  }

  ngOnDestroy(): void {
    this.onOrderChangeSub.unsubscribe();
  }

  createForm(){
    return this.formBuilder.group({
      orderID: [this.order.orderID],
      invoiceNumber: [this.order.invoiceNumber,[Validators.required]],
      orderDate : [this.order.orderDate],
      hardwareName : [this.order.hardwareName],
      productCode : [this.order.productCode],
      orderQuantity : [this.order.orderQuantity],
      orderAmount : [this.order.orderAmount],
      paymentMethod : [this.order.paymentMethod],
      paymentStatus : [this.order.paymentStatus]
    })
  }

}
<p>order-add-edit works!</p>

<mat-card class="example-card" >

  <mat-card-header>
    <mat-card-title>

      <span *ngIf="pageType == 'edit'">
        <h1>Edit Order</h1>
      </span>
      <span *ngIf="pageType == 'new'">
        <h1>New Order</h1>
      </span>

    </mat-card-title>
  </mat-card-header>

  <br>

  <mat-card-content>

    <form [formGroup]="orderForm">

      <div class="d-flex justify-content-around">

      <div class="mb-3" style="width: 98%;margin-left: 1%">
        <label  class="form-label">Invoice Number</label>
        <input class="form-control"  placeholder="Ex: WD00XX" formControlName="invoiceNumber">
      </div>
      <div style="width: 98%;margin-left: 1%; margin-right: 1%">
        <label  class="form-label"> Order Date</label>
        <input class="form-control"  class="form-control" type="date" formControlName="orderDate"/>
      </div>


      </div>

      <div class="mb-3" style="width: 98%; margin-left: 1%">
        <label  class="form-label">Hardware Name</label>
        <input type="text" class="form-control"  placeholder="Ex: Test Hardware" formControlName="hardwareName">
      </div>

      <div class="d-flex justify-content-around">


          <div class="mb-3" style="width: 32%">
            <label  class="form-label">Product Code</label>
            <input type="text" class="form-control"  placeholder="Ex: KSL15" formControlName="productCode">
          </div>
          <div class="mb-3" style="width: 32%">
            <label  class="form-label">Product Quantity</label>
            <input type="email" class="form-control"  placeholder="Ex: 500" formControlName="orderQuantity">
          </div>
          <div class="mb-3" style="width: 32%">
            <label  class="form-label">Product Amount</label>
            <input type="email" class="form-control"  placeholder="Ex: 15000" formControlName="orderAmount">
          </div>

      </div>

      <div style="margin-bottom: 2%;width: 99%" class="d-flex justify-content-between">

        <div style="width: 48%; margin-left: 1%">
          <label  class="form-label">Payment Method</label>
          <select class="form-select" aria-label="Default select example" formControlName="paymentMethod">
            <option value="1">CASH</option>
            <option value="2">CHEQUE</option>
            <option value="2">CREDIT</option>
          </select>
        </div>
        <div style="width: 48%; margin-left: 1%">
          <label  class="form-label">Payment Status</label>
          <select class="form-select" aria-label="Default select example" formControlName="paymentStatus">
            <option value="1">PAID</option>
            <option value="2">NOT PAID</option>
          </select>
        </div>

      </div>

      <div style="width: 8%; margin-right: 1%;margin-bottom: 2%;float: right">
        <div>
          <button type="button" class="btn btn-primary">Save</button>
        </div>
      </div>


    </form>

  </mat-card-content>

  <mat-card-footer>
    <mat-progress-bar mode="indeterminate"></mat-progress-bar>
  </mat-card-footer>
</mat-card>
