import { Component} from '@angular/core';
import {AuthService} from "../../services/auth.service";
import {Route, Router} from "@angular/router";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  loginForm : FormGroup;

  constructor(private authService: AuthService,
              private router : Router,
              private formBuilder : FormBuilder){

  }

  ngOnInit(): void{
    this.authService.loginStatus.subscribe((loginStatus) => {
      if (loginStatus){
        this.router.navigate(['/admin/dashboard'])
      } else {
        alert("Authentication Fail")
        // console.log("Authentication Error")
      }
    });

    this.loginForm = this.formBuilder.group({
      username : ['',Validators.required],
      password : ['',Validators.required]
    })

  }

  onLogin(){
    // console.log("click")
    // this.authService.login(false);

    let data = this.loginForm.getRawValue();
    console.log("data",data);

    if (data.username == 'admin' && data.password =='admin'){
      this.authService.login(true);
    } else {
      this.authService.login(false);
    }


  }



}

<div class="card" style="">
  <div class="card-body">
<div class="login-div">

  <h1>Login</h1>
  <form [formGroup]="loginForm">

    <div class="input-group mb-3">
      <span class="input-group-text" id="basic-addon1">@</span>
      <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" formControlName="username">
    </div>

    <div class="input-group mb-3">
      <span class="input-group-text" id="basic-addon2">@</span>
      <input type="password" class="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1" formControlName="password">
    </div>

  </form>

  <button type="button" class="btn btn-primary login-btn" (click)="onLogin()">Login</button>

</div>
  </div>
</div>
