import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {DashboardComponent} from "../dashboard/dashboard.component";

@Component({
  selector: 'app-admin-layout',
  templateUrl: './admin-layout.component.html',
  styleUrls: ['./admin-layout.component.css']
})
export class AdminLayoutComponent implements OnInit{

  userDetails: any = {};

  constructor(
    private router : Router
  ){}

  ngOnInit(){
    let user = sessionStorage.getItem('userDetails');
      // console.log("string",user)    // converted String file

    if (user != null){
      // console.log('user', JSON.parse(user)) //converted object from string
      this.userDetails = JSON.parse(user);
    }else{
      alert("Please Login Again")
      this.router.navigate(['/login'])
    }

  }

  logout(){

    this.router.navigate(['/login']);
    sessionStorage.removeItem('userDetails')

  }

}
<!--<p>admin-layout works!</p>-->

<div id="mySidenav" class="sidenav">

  <img src="assets/logopng.png" class="dashboard-logo" alt="">

  <div class="slide-nav-item" routerLinkActive="active"><a routerLink="/admin/dashboard" >Dashboard</a></div>
  <div class="slide-nav-item"><a routerLink="/admin/orders">Orders</a></div>
  <div class="slide-nav-item"><a routerLink="/admin/hardwares" >Hardwares</a></div>
  <div class="slide-nav-item"><a routerLink="/admin/products">Products</a></div>

<!--  <button routerLink="/login" type="button" class="btn btn-danger logout-btn" >Logout</button>-->
</div>


<div class="data-page">

  <div class="header">

    <h1>Hello {{userDetails.userName}}</h1>

    <button (click)="logout()" type="button"  class="btn btn-primary" >Logout</button>

  </div>

  <div class="card admin-layout">
    <div class="card-body">

  <router-outlet></router-outlet>

    </div>
  </div>

</div>
