import {Component, OnDestroy, OnInit} from '@angular/core';
import {HardwareAddEditService} from "../../../services/hardware/hardware-add-edit.service";
import {Subscription} from "rxjs";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {HardwareDTO} from "../dto/hardware";
import * as _ from 'underscore';

@Component({
  selector: 'app-hardware-add-edit',
  templateUrl: './hardware-add-edit.component.html',
  styleUrls: ['./hardware-add-edit.component.css']
})
export class HardwareAddEditComponent implements OnInit, OnDestroy{

  hardwareForm : FormGroup;
  hardware = new HardwareDTO();

  onHardwareChangeSub = new Subscription();

  constructor(
    private hardwareAddEditService : HardwareAddEditService,
    private formBuilder : FormBuilder

  ){}

  ngOnInit(): void {
    this.onHardwareChangeSub = this.hardwareAddEditService.hardware
      .subscribe((hardware) => {
        if (!_.isEmpty(hardware)){
          this.hardware = new HardwareDTO(hardware);
        }else {
          this.hardware = new HardwareDTO();
        }
      // console.log("hardware" , this.hardware)
        this.hardwareForm = this.createForm();
    })
  }

  ngOnDestroy(): void {
    this.onHardwareChangeSub.unsubscribe();
  }

  createForm(){
    return this.formBuilder.group({
      hardwareID: [this.hardware.hardwareID],
      hardwareName: [this.hardware.hardwareName,[Validators.required]],
      hardwareAddress : [this.hardware.hardwareAddress],
      hardwareTel : [this.hardware.hardwareTel],
      hardwareEmail : [this.hardware.hardwareEmail],
      hardwareStatus : [this.hardware.hardwareStatus]
    })
  }

}
<p>hardware-add-edit works!</p>

<mat-card class="example-card">
  <mat-card-header>
    <mat-card-subtitle>Dog Breed</mat-card-subtitle>
    <mat-card-title>Shiba Inu</mat-card-title>
  </mat-card-header>
  <mat-card-content>
    <p>This card has divider and indeterminate progress as footer</p>
    <mat-divider></mat-divider>
  </mat-card-content>
  <mat-card-actions>
    <button mat-button>LIKE</button>
    <button mat-button>SHARE</button>
  </mat-card-actions>
  <mat-card-footer>
    <mat-progress-bar mode="indeterminate"></mat-progress-bar>
  </mat-card-footer>
</mat-card>

<form action="">

  <div class="mb-3" style="width: 98%;margin-left: 1%">
    <label  class="form-label">Hardware Name</label>
    <input class="form-control"  placeholder="Ex: XXXXX Hardware">
  </div>

  <div class="d-flex justify-content-around">

    <div class="mb-3" style="width: 48%">
      <label  class="form-label">Hardware Address</label>
      <textarea class="form-control"  rows="5" placeholder="Ex: Kurunegala"></textarea>
    </div>

    <div style="width: 48%">
      <div class="mb-3" >
        <label  class="form-label">Hardware Tel</label>
        <input type="text" class="form-control"  placeholder="Ex: 077XXXXXXX">
      </div>
      <div class="mb-3" >
        <label  class="form-label">Hardware Email</label>
        <input type="email" class="form-control"  placeholder="Ex: name@example.com">
      </div>
    </div>
  </div>
  <div style="width: 48%; margin-left: 1%">
  <select class="form-select" aria-label="Default select example">
    <option selected>Select Hardware Status</option>
    <option value="1">ACTIVE</option>
    <option value="2">DEACTIVE</option>
  </select>
  </div>

</form>
