import {Component, OnDestroy, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {ProductAddEditService} from "../../../services/product/product-add-edit.service";
import * as _ from 'underscore';
import {ProductDTO} from "../dto/product";
import {Subscription} from "rxjs";
import {formatNumber, Location} from "@angular/common";
import {_isNumberValue} from "@angular/cdk/coercion";

@Component({
  selector: 'app-product-add-edit',
  templateUrl: './product-add-edit.component.html',
  styleUrls: ['./product-add-edit.component.css']
})
export class ProductAddEditComponent implements OnInit, OnDestroy{

  productForm : FormGroup;
  product = new ProductDTO();
  pageType: string;

  onProductChangeSub = new Subscription();

  constructor(
    private productAddEditService : ProductAddEditService,
    private formBuilder : FormBuilder,
    private location :Location

){}

  ngOnInit(): void {
    this.onProductChangeSub = this.productAddEditService.product
      .subscribe((product) => {
        if (!_.isEmpty(product)){
          // console.log(product);
          this.product = new ProductDTO(product);
          this.pageType ='edit';
        }else {
          this.product = new ProductDTO();
          this.pageType = 'new';
        }
        // console.log("hardware" , this.hardware)
        this.productForm = this.createForm();
      })
  }

  ngOnDestroy(): void {
    this.onProductChangeSub.unsubscribe();
}

  createForm(){
    return this.formBuilder.group({
      productID: [this.product.productID],
      productCode: [this.product.productCode,[Validators.required]],
      productName : [this.product.productName,[Validators.required]],
      productSize : [this.product.productSize,[Validators.required]],
      productPrice : [this.product.productPrice,[Validators.required]],
      productStatus : [this.product.productStatus,[Validators.required]]
    })
  }

  isValid(){
    return this.productForm.valid;
  }

  isDirty(){
    return this.productForm.dirty;
  }

  goBack(){
    // console.log("click")
    // this.router.navigate(['admin/orders']) //using router link
    this.location.back();
  }

  saveProduct(){
    let product = this.productForm.getRawValue();
    console.log("get data", product);
      this.productAddEditService.saveProduct(product);
    if (product){
      // console.log(product);
      alert("Data Saved");
      this.location.back()
    }
  }

}




  <mat-card-header>
    <mat-card-title>

      <div style="display: flex">

      <svg xmlns="http://www.w3.org/2000/svg" class="back-btn" (click)="goBack()" viewBox="0 0 512 512"><!--! Font Awesome Pro 6.3.0 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2023 Fonticons, Inc. --><path d="M512 256A256 256 0 1 0 0 256a256 256 0 1 0 512 0zM231 127c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9l-71 71L376 232c13.3 0 24 10.7 24 24s-10.7 24-24 24l-182.1 0 71 71c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0L119 273c-9.4-9.4-9.4-24.6 0-33.9L231 127z"/></svg>


      <span *ngIf="pageType == 'edit'">
        <h1>Edit Product</h1>
      </span>
      <span *ngIf="pageType == 'new'">
        <h1>New Product</h1>
      </span>

      </div>
    </mat-card-title>
  </mat-card-header>

  <br>

  <mat-card-content>

    <form [formGroup]="productForm">

      <div class="d-flex justify-content-around">

      <div class="mb-3" style="width: 98%;margin-left: 1%">
        <label  class="form-label">Product Code</label>
        <input class="form-control"  placeholder="Ex: KSL15" formControlName="productCode">
      </div>
      <div class="mb-3" style="width: 98%;margin-left: 1%">
        <label  class="form-label">Product Name</label>
        <input class="form-control"  placeholder="Ex: Keshara Super Lime " formControlName="productName">
      </div>

      </div>

      <div class="d-flex justify-content-around">


          <div class="mb-3" style="width: 98%;margin-left: 1%">
            <label  class="form-label">Product Size</label>
            <input type="text" class="form-control"  placeholder="Ex: 20KG" formControlName="productSize">
          </div>
          <div class="mb-3" style="width: 98%;margin-left: 1%">
            <label  class="form-label">Product Price</label>
            <input type="number" class="form-control"  placeholder="Ex: 1450.00" formControlName="productPrice">
          </div>

      </div>

      <div style="margin-bottom: 2%;width: 99%" class="d-flex justify-content-between">
        <div style="width: 48%; margin-left: 1%">
          <label  class="form-label">Product Status</label>
          <select class="form-select" aria-label="Default select example" formControlName="productStatus">
            <option selected value="ACTIVE">ACTIVE</option>
            <option value="DEACTIVE">DEACTIVE</option>
          </select>
        </div>
        <div>

        </div>

      </div>

      <div style="margin-bottom: 2%; float: right">
        <button type="button" class="btn btn-primary save-btn" *ngIf="pageType == 'edit'" (click)="saveProduct()" [disabled]="!isDirty()">
          Update
        </button>
        <button type="button" class="btn btn-primary save-btn" *ngIf="pageType == 'new'" (click)="saveProduct()" [disabled]="!isValid()">
          Save
        </button>
      </div>

    </form>

  </mat-card-content>

