import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {DashboardService} from "../../services/dashboard/dashboard.service";

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{

  dashboardValues : any;


  constructor(private dashboardService : DashboardService,
              private router :Router,
  ){}

  ngOnInit(): void {
    this.dashboardService.getValues();
    this.dashboardService.dashboardValues
      .subscribe((values) =>{
        // console.log("values in dashboard",values);
        if (values){
          this.dashboardValues = values;
        }
      })

  }

  getValues(){
    this.dashboardService.getValues();
  }


}
<p>Counts</p>

<div style="display: flex; margin-bottom: 2%" >

  <div class="card square-box" style="background-color: cadetblue">
    <div class="card-body">
      <p>{{dashboardValues.orderCount}}</p>
    </div>
  </div>

  <div class="card square-box" style="background-color: aquamarine">
    <div class="card-body">
      <p>{{dashboardValues.hardwareCount}}</p>
    </div>
  </div>

  <div class="card square-box" style="background-color: aqua">
    <div class="card-body">
      <p>{{dashboardValues.productCount}}</p>
    </div>
  </div>

</div>

<p>Counts</p>

<div style="display: flex; margin-bottom: 2%">

  <div class="card square-box2" style="background-color: cadetblue">
    <div class="card-body">
      <p>{{dashboardValues.totalRevenue}}</p>
    </div>
  </div>

  <div class="card square-box2" style="background-color: cadetblue">
    <div class="card-body">
      <p>{{dashboardValues.cashRevenue}}</p>
    </div>
  </div>

  <div class="card square-box2" style="background-color: cadetblue">
    <div class="card-body">
      <p>{{dashboardValues.chequeRevenue}}</p>
    </div>
  </div>

  <div class="card square-box2" style="background-color: cadetblue">
    <div class="card-body">
      <p>{{dashboardValues.creditRevenue}}</p>
    </div>
  </div>

</div>
