import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {ProductDTO} from "../../product/dto/product";
import {UserDTO} from "../dto/user";
import {ProductAddEditService} from "../../../services/product/product-add-edit.service";
import {UserAddEditService} from "../../../services/user/user-add-edit.service";
import {Location} from "@angular/common";
import {Router} from "@angular/router";

@Component({
  selector: 'app-user-add-edit',
  templateUrl: './user-add-edit.component.html',
  styleUrls: ['./user-add-edit.component.css']
})
export class UserAddEditComponent implements OnInit{

  userForm : FormGroup;
  user = new UserDTO();

  constructor(
    private formBuilder : FormBuilder,
    private userAddEditService : UserAddEditService,
    private router :Router,
    private location :Location
  ){}

  ngOnInit(): void {
    this.userForm = this.createForm();
  }

createForm() {
  return this.formBuilder.group({
    userID: [this.user.userID],
    userName: [this.user.userName,[Validators.required]],
    userPassword : [this.user.userPassword,[Validators.required]],
    userStatus : [this.user.userStatus,[Validators.required]],
  })
  }


  isValid(){
    return this.userForm.valid;
  }

  saveUser(){
    let user = this.userForm.getRawValue();
    console.log("get data", user);
    this.userAddEditService.saveUser(user);
    if (user){
      // console.log(product);
      alert("User Saved");
      this.location.back()
    }
  }


    onClear(){
      this.userForm.setValue({
        userName:'',
        userPassword:'',
        userStatus:''
      });

      this.saveUser();

  }

}
<div>

<!--  //add user-->
  <div>

    <h1>Add User</h1>

    <form action="" [formGroup]="userForm">

    <div style="display: flex">
<!--    username-->
    <div style="width: 32%">
      <label  class="form-label">Username</label>
      <input class="form-control"  placeholder="username" formControlName="userName" type="text">
    </div>

    <!--    password-->
    <div style="width: 32%; margin-left: 1%">
      <label  class="form-label">Password</label>
      <input class="form-control"  placeholder="password" formControlName="userPassword" type="password">
    </div>

      <!--    status-->
      <div style="width: 32%; margin-left: 1%">
        <label  class="form-label">User Status</label>
        <select class="form-select" aria-label="Default select example" formControlName="userStatus">
          <option selected value="ACTIVE">ACTIVE</option>
          <option value="DEACTIVE">DEACTIVE</option>
        </select>
      </div>

    </div>

    <br>

<!--    save user btn-->
    <div >

      <button class="btn btn-primary clear-btn" style="margin-left: 10px" (click)="onClear()">
        Clear
      </button>

      <button type="button" class="btn btn-primary save-btn" (click)="saveUser()" [disabled]="!isValid()">
        Save
      </button>
    </div>

    </form>



  </div>

<!--  update User-->
  <div>

<!--    <h1>Update User</h1>-->

    <div>

    </div>

    <div>

    </div>

  </div>

</div>
