import {Component, OnDestroy, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {HardwareDTO} from "../../hardware/dto/hardware";
import {ProductAddEditService} from "../../../services/product/product-add-edit.service";
import * as _ from 'underscore';
import {ProductDTO} from "../dto/product";

@Component({
  selector: 'app-product-add-edit',
  templateUrl: './product-add-edit.component.html',
  styleUrls: ['./product-add-edit.component.css']
})
export class ProductAddEditComponent implements OnInit, OnDestroy{

  productForm : FormGroup;
  product = new productDTO();
  pageType: string;

  onProductChangeSub = new Subscription();

  constructor(
    private productAddEditService : ProductAddEditService,
    private formBuilder : FormBuilder

){}

  ngOnInit(): void {
    this.onProductChangeSub = this.productAddEditService.product
      .subscribe((product) => {
        if (!_.isEmpty(product)){
          console.log(product);
          this.product = new ProductDTO(product);
          this.pageType ='edit';
        }else {
          this.product = new ProductDTO();
          this.pageType = 'new';
        }
        // console.log("hardware" , this.hardware)
        this.productForm = this.createForm();
      })
  }

  ngOnDestroy(): void {
    this.onProductChangeSub.unsubscribe();
}

  createForm(){
    return this.formBuilder.group({
      productID: [this.product.productID],
      productCode: [this.product.productCode,[Validators.required]],
      productName : [this.product.productName],
      productSize : [this.product.productSize],
      productPrice : [this.product.productPrice],
      productStatus : [this.product.productStatus]
    })
  }

}

<p>product-add-edit works!</p>

<mat-card class="example-card" >

  <mat-card-header>
    <mat-card-title>

      <span *ngIf="pageType == 'edit'">
        <h1>Edit Product</h1>
      </span>
      <span *ngIf="pageType == 'new'">
        <h1>New Product</h1>
      </span>

    </mat-card-title>
  </mat-card-header>

  <br>

  <mat-card-content>

    <form [formGroup]="productForm">

      <div class="mb-3" style="width: 98%;margin-left: 1%">
        <label  class="form-label">Hardware Name</label>
        <input class="form-control"  placeholder="Ex: XXXXX Hardware" formControlName="hardwareName">
      </div>

      <div class="d-flex justify-content-around">

        <div class="mb-3" style="width: 48%">
          <label  class="form-label">Hardware Address</label>
          <textarea class="form-control"  rows="5" placeholder="Ex: Kurunegala" formControlName="hardwareAddress"></textarea>
        </div>

        <div style="width: 48%">
          <div class="mb-3" >
            <label  class="form-label">Hardware Tel</label>
            <input type="text" class="form-control"  placeholder="Ex: 077XXXXXXX" formControlName="hardwareTel">
          </div>
          <div class="mb-3" >
            <label  class="form-label">Hardware Email</label>
            <input type="email" class="form-control"  placeholder="Ex: name@example.com" formControlName="hardwareEmail">
          </div>
        </div>
      </div>

      <div style="margin-bottom: 2%;width: 99%" class="d-flex justify-content-between">
        <div style="width: 48%; margin-left: 1%">
          <select class="form-select" aria-label="Default select example" formControlName="hardwareStatus">
            <option selected>Select Hardware Status</option>
            <option value="1">ACTIVE</option>
            <option value="2">DEACTIVE</option>
          </select>
        </div>
        <div>
          <button type="button" class="btn btn-primary">Save</button>
        </div>
      </div>

    </form>

  </mat-card-content>

  <mat-card-footer>
    <mat-progress-bar mode="indeterminate"></mat-progress-bar>
  </mat-card-footer>
</mat-card>
