import { Component} from '@angular/core';
import {AuthService} from "../../services/auth.service";
import {Route, Router} from "@angular/router";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  loginForm : FormGroup;

  constructor(private authService: AuthService,
              private router : Router,
              private formBuilder : FormBuilder){

  }

  ngOnInit(): void{
    this.authService.loginStatus.subscribe((userDetails) => {
      if (userDetails){
        this.router.navigate(['/admin/dashboard'])
        sessionStorage.setItem('userDetails', JSON.stringify(userDetails)) //stringify is used for convert json to string value
      } else if (userDetails == null) {
        alert("Authentication Fail")
        // console.log("Authentication Error")
      }
    });

    this.loginForm = this.formBuilder.group({
      userName : ['',Validators.required],
      passWord : ['',Validators.required]
    })

  }

  onLogin(){
    let data = this.loginForm.getRawValue();

    // console.log("click")
    this.authService.login(data);


    // console.log("data",data);

    // if (data.username == 'admin' && data.password =='admin'){
    //   this.authService.login(true);
    // } else {
    //   this.authService.login(false);
    // }


  }

  //login btn validator only activate when user fill the username and password both
  isValid(){

    return this.loginForm.valid;

  }

  logout(){

  }

}

<div class="background-image">
</div>

<div fxLayoutAlign="center center" fxFlexFill style="margin-top: 20%">
  <div class="card login-card" style="margin-top: 15%">
    <div class="card-body">

<div class="login-div" fxLayout="column"  >

  <h1 align="center">Login</h1>
  <form [formGroup]="loginForm">

    <div class="input-group mb-3">
      <span class="input-group-text" id="basic-addon1">
        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-person" viewBox="0 0 16 16">
        <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6Zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0Zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4Zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10Z"/>
        </svg>
      </span>
      <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" formControlName="userName">
    </div>

    <div class="input-group mb-3">
      <span class="input-group-text" id="basic-addon2">
        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-key" viewBox="0 0 16 16">
        <path d="M0 8a4 4 0 0 1 7.465-2H14a.5.5 0 0 1 .354.146l1.5 1.5a.5.5 0 0 1 0 .708l-1.5 1.5a.5.5 0 0 1-.708 0L13 9.207l-.646.647a.5.5 0 0 1-.708 0L11 9.207l-.646.647a.5.5 0 0 1-.708 0L9 9.207l-.646.647A.5.5 0 0 1 8 10h-.535A4 4 0 0 1 0 8zm4-3a3 3 0 1 0 2.712 4.285A.5.5 0 0 1 7.163 9h.63l.853-.854a.5.5 0 0 1 .708 0l.646.647.646-.647a.5.5 0 0 1 .708 0l.646.647.646-.647a.5.5 0 0 1 .708 0l.646.647.793-.793-1-1h-6.63a.5.5 0 0 1-.451-.285A3 3 0 0 0 4 5z"/>
        <path d="M4 8a1 1 0 1 1-2 0 1 1 0 0 1 2 0z"/>
        </svg>
      </span>
      <input type="password" class="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1" formControlName="passWord">
    </div>

  </form>
  <br>

  <button type="button" class="btn btn-primary login-btn"  (click)="onLogin()" [disabled]="!isValid()">Login</button>

  <br>

</div>

